import React from 'react';
import Load from './loading.gif';
import Concern from '../concern/concern';
export default class Loading extends React.Component {
  constructor(props) {
    super();
    this.state = {}
  }
  render() {
    return (
      <div   style={{
        width: 261,
        height: 295,
        position: 'absolute',

      }}>
        <Concern ref={ref=>this.dream=ref}/>
        <span ref={ref => this._load = ref} style={{
          width: 16,
          height: 16,
          background: `url(${Load}) no-repeat`,
          display: 'block',
          margin: '140px auto',
          backgroundSize:'100% 100%'
        }}></span>

      </div>
    )
  }
  componentDidMount() {
    window.onload = (() => {
      setTimeout(() => {
       this._d
     },100)
      this._load.style.display='none'
    })
  }
}
