import React from 'react';
import music from './music.png';
import gold from './gold.png';
class Process extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [
        { name: "绿钻豪华版：未开通", val: 50, text: ['LV0', 'LV1'] },
        // { name: "付费音乐包：未开通", val: 90, text: ['LV01', 'LV2'] }
      ]
    }
  }
  render() {
    return (
      <div>
        {
          this.state.data.map((s, i) => {
            let bg = null;
            if (i == 0) {
              bg = gold
            } else if (i == 1) {
              bg = music
            }
            return <div style={{
              borderBottom: '1px solid #ededed'
            }} key={s + i}>

              <div style={{
                display: 'flex',
                boxSizing: 'border-box',
                padding: 10

              }}>
                <span style={{
                  width: 16,
                  height: 16,
                  background: `url(${bg}) no-repeat`,
                  display: 'block',
                  marginLeft: 20
                }}></span>
                <div id='container' ref={ref => this.container = ref} style={{
                  width:172
                }}>
                  <span ref={ref => this._circle0 = ref} style={{
                    borderColor: '#f0f0f0'
                  }}></span>
                  <span ref={ref => this._circle1 = ref} style={{
                    borderColor: '#f0f0f0'
                  }} ></span>
                  <div className="process-wrap" ref={ref => this._process = ref} style={{
                    width: 0
                  }}></div>
                  <div className="waterPop"></div>
                </div>
              </div>
              <div style={{ fontSize: 12, color: '#666',marginBottom:20 }}>绿钻豪华版：未开通</div>
            </div>
          })
        }

      </div>
    )
  }
  componentDidMount() {
    const me = this;
    let num = 0;
    let w = parseFloat(this.container.style.width);
    let timer = setInterval(() => {
      num += 1;
      if (num <= 90) {
        let disx = this._process.style.width = (num / 100 * w);


        if (35 < disx && disx <= 135) {
          this._circle0.style.borderColor = '#62bf82'
        }
        else if (disx > 135) {
          this._circle1.style.borderColor = '#62bf82'
        }
        this._process.style.width = disx + 'px';
      } else {
        clearInterval(timer)
      }

    }, 60)
  }
}
export default Process;
